mapper.select:
SELECT ID, TITLE, WRITERID, CONTENT, REGDATE, HIT, FILES
FROM (SELECT ROWNUM NUM, N.* FROM (SELECT * FROM NOTICE WHERE ${field}
LIKE '%'|| #{query} ||'%' ORDER BY REGDATE DESC)N)
WHERE NUM BETWEEN (#{page} - 1) * #{records} AND #{page} * #{records}

SELECT ID, TITLE, WRITERID, CONTENT, REGDATE, HIT, FILES
FROM NOTICE
WHERE ID = #{id}
